import React, { useState } from "react"

export default function NewDrink({ onAddDrink, mixologists }){ // , mixologist
  const [name, setName] = useState("")
  // ingredients
  const [instructions, setInstructions] = useState("")
  const [mixologistId, setMixologistId] = useState("")
  // const mixologistId = mixologist.id
  // console.log(mixologistId)

  function handleSubmit(e){
    e.preventDefault()
    window.alert(`${name} added to drinks!`)

    const drinkData = {
      name: name,
      instructions: instructions,
      mixologist_id: mixologistId // mixologist.id
    }
    console.log(`New drink obj for fetch: ${drinkData}`)
    console.log(JSON.stringify(drinkData))

    fetch("http://localhost:9292/drinks", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(drinkData),
    })
      .then(r => r.json())
      .then(newDrink => {
        onAddDrink(newDrink)
        setName("")
        setInstructions("")
      })
  }

  console.log(mixologistId)
  const options =  mixologists.map(mixologist => { 
    return <option 
      // onChange={e => setName(e.target.value)}
      onChange={e => console.log(e.target.value)}
      key={mixologist.id} 
      value={mixologistId}
      // type={}
    >
      {mixologist.name}
    </option>
  })


  return(
    <div className="Form-wrapper">
      {/* <a className="App-link" href="#" >
        Add new drink
      </a> */}
      <h2>Add new drink</h2>
      <form className="Form" onSubmit={handleSubmit} >
        <label>Name
          <input
            onChange={e => setName(e.target.value)}
            placeholder="Drink name"
            type="text"
            // name="name"
            value={name}
            className="Form-input"
        />
        </label>
        <label>Instructions
          <input
            onChange={e => setInstructions(e.target.value)}
            type="text"
            placeholder="Sugar, spice, everything nice..."
            // name="instructions"
            value={instructions}
            className="Form-input"
          />
        </label>

        <label>Created by
  <select>
    {options}
    <option value="option 1" >one</option>
  </select>
  </label>


        <div className="Button-wrapper">
          <button type="submit" className="Button">Add drink</button>
        </div>
      </form>

    </div>
  )
}
