  }

  const renderDrinks = drinks.map(drink => console.log(drink))

  return(
    <div>
      <a className="App-link" onClick={handleMixologistClick}>
        {mixologist.name}
      </a>
      <>{renderDrinks}</>

    </div>
  )
}
