export default function EditDrink({ drink }){
  const { name } = drink
  const [drinkName, setDrinkName] = useState(name)
  return(
    <form>
      <input 
        className="Form-input" 
        type="text"
        name="name"
        value={drinkName}
        onChange={e => setDrinkName(e.target.value)}      
      />
      <input 
        className="Form-input"
      />
    </form>
  )
}
