  const drinksArr = mixologistObj.drinks

  const renderDrinks = drinksArr.map(drink => {
    return <Drink 
      key={drink.id} 
      drink={drink} 
      onUpdateDrink={onUpdateDrink}
      onDeleteDrink={onDeleteDrink}
      mixologist={mixologist}
    />
