import React, { useState } from "react"

export default function NewDrink({ onAddDrink, mixologists }){
  const [name, setName] = useState("") // ingredients
  const [instructions, setInstructions] = useState("")
  const [mixologistId, setMixologistId] = useState("")
  // console.log(mixologistId)

  function handleSubmit(e){
    e.preventDefault()
    window.alert(`${name} added!`)

    const drinkData = {
      name: name,
      instructions: instructions,
      mixologist_id: mixologist.id
    }

    fetch("http://localhost:9292/drinks", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(drinkData),
    })
      .then(r => r.json())
      .then(newDrink => {
        onAddDrink(newDrink)
        setName("")
        setInstructions("")
      })
  }

  // console.log(mixologistId)
  const options =  mixologists.map(mixologist => { 
    return  <option 
              key={mixologist.id} 
              value={mixologist.id}
            >
              {mixologist.name}
            </option>
  })


  return(
    <div className="Form-wrapper">
      <a className="App-link" href="#" >
        Add new drink
      </a> {/* <h2>Add new drink</h2> */}
      <form className="Form" onSubmit={handleSubmit} >
        <label>Name
          <input
            onChange={e => setName(e.target.value)}
            placeholder="Drink name"
            type="text"
            value={name}
            className="Form-input"
        />
        </label>
        <label>Instructions
          <input
            onChange={e => setInstructions(e.target.value)}
            type="text"
            placeholder="Sugar, spice, everything nice..."
            value={instructions}
            className="Form-input"
          />
        </label>

        <label>
          Created by:
          <select className="Dropdown">
            {options}
          </select>
        </label>


        <div className="Button-wrapper">
          <button type="submit" className="Button">Add drink</button>
        </div>


      </form>

    </div>
  )
}
