import React, { useState } from "react"

export default function NewDrink({ mixologists }){
  // onAddDrink
  const [name, setName] = useState("")
  const [ingredients, setIngredients] = useState("")
  const [instructions, setInstructions] = useState("")
  const [mixologistId, setMixologistId] = useState()(mixologists[0][id])

  console.log(mixologistId)
  // console.log(mixologists)

  const options =  mixologists.map(mixologist => { 
    return <option  
            key={mixologist.id} 
            value={mixologist.id}
          >
            {mixologist.name}
          </option>
  }) 

  return(
    <div>
      <h2>Add new drink</h2>
      <form className="Form" >
      {/* onSubmit={handleSubmit}  */}
        <label>Name
          <input
            onChange={e => setName(e.target.value)} 
            placeholder="Drink name" 
            className="Form-input"
          />
    {/* type="text"
  //           value={name}
  //           className="Form-input" */}
        </label>
        <label>Ingredients
          <input 
            onChange={e => setIngredients(e.target.value)}
            placeholder="Sugar, spice, everything nice" 
            className="Form-input"  
          /> 
        </label>
        <label>Instructions
          <input 
            onChange={e => setInstructions(e.target.value)}
            placeholder="First things first..." 
            className="Form-input"  
          /> 
        {/* type="text" */}
        {/* value={instructions} */}
        </label>
        <label>
          Created by:
          <select 
            onChange={e => setMixologistId(e.target.value)}
            // value={mixologistId} 
            className="Dropdown"
          >
            {options}
          </select>
        </label>

        <div className="Button-wrapper">
          <button type="submit" className="Button">Add drink</button>
        </div>
      </form>
    </div>
  )
}

